package hybrid;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.mongodb.core.MongoTemplate;import org.springframework.data.mongodb.repository.config.EnableMongoRepositories;import org.springframework.stereotype.Service;/** * @author vyarmole on 06.04.17. */@Service@EnableMongoRepositories(basePackages = "hybrid")public class MongoService {  public MongoService() {  }  private Logger LOGGER = LoggerFactory.getLogger(MongoService.class);  @Autowired  private MongoRepository mongoRepository;  @Autowired  private MongoTemplate mongoTemplate;  public void save(String object) {    LOGGER.info("mongoRepository = " + mongoRepository);    LOGGER.info("mongoTemplate = " + mongoTemplate);    Model model = new Model();    model.setId(System.currentTimeMillis());    model.setValue(object);    mongoRepository.save(model);    LOGGER.info("Saved: " + object);  }}