package me.jarad.capella.application;import org.reflections.Reflections;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import java.util.HashMap;import java.util.Map;import java.util.Set;/** * @author vyarmole on 10.04.17. */public class ApplicationInitialization {  private static Logger LOGGER = LoggerFactory.getLogger(ApplicationInitialization.class);  public static Map<Class, Class> fillServices() {    Map<Class, Class> entityService = new HashMap<>();    Reflections reflections = new Reflections("me.jarad.capella");    Class<EntityName> annotation = EntityName.class;    Set<Class<?>> typesAnnotatedWith = reflections.getTypesAnnotatedWith(annotation);    for (Class<?> clz : typesAnnotatedWith) {      EntityName entityAnnotation = clz.getAnnotation(annotation);      Class entityClass = entityAnnotation.entityClass();      entityService.put(entityClass, clz);    }    return entityService;  }}