package me.jarad.capella.model.security;import com.fasterxml.jackson.annotation.JsonInclude;import org.springframework.data.annotation.Id;/** * @author vyarmole on 07.04.17. */@JsonInclude(JsonInclude.Include.NON_NULL)public class User {  @Id  private String id;  private String name;  public String getPwd() {    return pwd;  }  public void setPwd(String pwd) {    this.pwd = pwd;  }  private String pwd;  public String getId() {    return id;  }  public void setId(String id) {    this.id = id;  }  public String getName() {    return name;  }  public void setName(String name) {    this.name = name;  }}