package me.jarad.capella.persistance;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.mongodb.core.MongoTemplate;import org.springframework.stereotype.Service;/** * @author vyarmole on 06.04.17. */@Servicepublic class MongoService {  private Logger LOGGER = LoggerFactory.getLogger(MongoService.class);  @Autowired  private ModelMongoRepository mongoRepository;  @Autowired  private MongoTemplate mongoTemplate;  public void save(String object) {    Model model = new Model();    model.setId(System.currentTimeMillis());    model.setValue(object);    mongoRepository.save(model);    LOGGER.info("Saved: " + object);  }  public ModelMongoRepository getMongoRepository() {    return mongoRepository;  }  public void setMongoRepository(ModelMongoRepository mongoRepository) {    this.mongoRepository = mongoRepository;  }  public MongoTemplate getMongoTemplate() {    return mongoTemplate;  }  public void setMongoTemplate(MongoTemplate mongoTemplate) {    this.mongoTemplate = mongoTemplate;  }}