package me.jarad.capella.persistance.services;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.mongodb.core.MongoTemplate;import org.springframework.stereotype.Service;import me.jarad.capella.persistance.Model;import me.jarad.capella.persistance.repository.ModelMongoRepository;/** * @author vyarmole on 06.04.17. */@Servicepublic class MongoService {  private Logger LOGGER = LoggerFactory.getLogger(MongoService.class);  @Autowired  private ModelMongoRepository modelRepository;  @Autowired  private MongoTemplate mongoTemplate;  public void save(String object) {    Model model = new Model();    model.setId(System.currentTimeMillis());    model.setValue(object);    modelRepository.save(model);    LOGGER.info("Saved: " + object);  }}