package me.jarad.capella.persistance.services;import com.google.common.collect.Lists;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Collection;import me.jarad.capella.model.security.User;import me.jarad.capella.persistance.repository.UserRepository;/** * @author vyarmole on 07.04.17. */@Servicepublic class UserService {  @Autowired  private UserRepository userRepository;  public Collection<User> getUsers() {    Iterable<User> all = userRepository.findAll();    Collection<User> users = Lists.newArrayList(all.iterator());    return users;  }  public void save(User user) {    userRepository.save(user);  }}