package me.jarad.capella.persistance.services;import com.google.common.collect.Lists;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Collection;import me.jarad.capella.model.security.User;import me.jarad.capella.persistance.repository.UserRepository;/** * @author vyarmole on 07.04.17. */@Servicepublic class UserService {  private Logger LOGGER = LoggerFactory.getLogger(MongoService.class);  @Autowired  private UserRepository userRepository;  public Collection<User> getUsers() {        LOGGER.info("userRepository = " + userRepository);    Iterable<User> all   = userRepository.findAll();    LOGGER.info("all = " + all);    Collection<User> users = Lists.newArrayList(all.iterator());    return users;  }  public void save(User user) {    userRepository.save(user);  }  public User getUser(String id) {    return userRepository.findOne(id);  }  public void removeUser(String id) {    userRepository.delete(id);  }  public User getUserByName(String filterText) {    return userRepository.findByNameStartsWithIgnoreCase(filterText);  }}