package me.jarad.capella.ui.view;import com.vaadin.data.provider.DataProvider;import com.vaadin.navigator.View;import com.vaadin.navigator.ViewChangeListener;import com.vaadin.spring.annotation.SpringView;import com.vaadin.ui.Grid;import com.vaadin.ui.VerticalLayout;import org.springframework.beans.factory.annotation.Autowired;import me.jarad.capella.model.security.User;import me.jarad.capella.persistance.services.UserService;/** * @author vyarmole on 07.04.17. */@SpringView(name = "security")public class SecurityView extends VerticalLayout implements View {  @Autowired  UserService userService;  public SecurityView() {    setMargin(true);    VerticalLayout mainLayout = new VerticalLayout();    Grid<User> userGrid = new Grid<>();    userGrid.addColumn("id");    userGrid.addColumn("name");    userGrid.addColumn("pwd");    userGrid.setDataProvider(DataProvider.ofCollection(userService.getUsers()));    mainLayout.addComponent(userGrid);    /*HorizontalLayout buttons = new HorizontalLayout();    buttons.addComponent(            new Button("Add user", event -> {              userService.save("Object1");    }));    buttons.setSpacing(true);    mainLayout.addComponent(buttons);*/    addComponent(mainLayout);  }  @Override  public void enter(ViewChangeListener.ViewChangeEvent event) {  }}